import { Component } from '@angular/core';
import { Student, Years } from '../../models/student.model';
import { Test } from '../../models/test.model';
import { StudentService } from '../../student.service';
import { Router } from '@angular/router';

@Component({
  selector: 'student-list',
  templateUrl: './student-list.component.html'

})
export class StudentListComponent {

  selectedTest!: Test[];
  newStudent!: boolean;
  //לסדר מחיקה מול השרת
  deleatStudent(student: string) {
    for (let s of this.students) {
      if (s.id == student) {
        let studentToDeleate = s;
        let studentToDeleate1 = this.students.indexOf(studentToDeleate);
        this.students.splice(studentToDeleate1, 1);
      }
    }
  }

  // showTest(tests: Test[], id: string) {
  //   this.selectedTest = tests;
  //   this.idS = id;

  // }

  addStudent() {
      this._router.navigate(["/addStudent"]);
  }

  getFromServer(){
    this._studentService.getFromServer().subscribe(data=>{
      this.students=data;
     });
  }

  getFromServerByActive(active:boolean){
    this._studentService.getFromServerByActive(active).subscribe(data=>{
      this.students=data;
    });
  }


  putInServer(id:number, student:Student){
    this._studentService.putInServer(id,student).subscribe(data=>{
      if(data){
        this.getFromServer();
       }
       else{
        alert("the update didn't succeed")
       }
      })
  }
  deleatFromServer(){

  }
  constructor(private _studentService: StudentService,private _router:Router) {
  }
  students!: Student[];

  ngOnInit(){
    this._studentService.getFromServer().subscribe(data=>{
      this.students=data;
     });
  }
}

<input type="checkbox" #cb (change)="getFromServerByActive(cb.checked)"/>הצגת רק תלמידים פעילים<br>
<ul>
    <li *ngFor="let student of students" [ngClass]="{'active':!student.active,'highmark': student.marksAvg <= 90 }">
      id: {{student.id}}, firstname: {{student.firstname}}, lastname: {{student.lastname}}, address: {{student.address}},
       telephone: {{student.telephone}}, marksAvg: {{student.marksAvg}},
       <p *ngIf="student.active && student.absentDays[0].sumdays>0"> you were absant {{student.absentDays[0].sumdays}} days </p>
        <button (click)="deleatStudent(student.id)" >למחיקת התלמיד</button>
        <button routerLink="/students/{{student.idS}}" >עריכת התלמיד</button>
        <!-- <button *ngIf="student.testList && student.id" (click)="showTest(student.testList,student.id)">הצג בחינות</button> -->
        <button *ngIf="student.testList && student.id" >הצג בחינות</button>

    </li>
</ul>
<button (click)="addStudent()">להוספת תלמיד</button>
<test-list [testList]="selectedTest"></test-list>
