import { Component, Input } from '@angular/core';
import { Test } from '../../models/test.model';
import { StudentService } from '../../student.service';


@Component({
  selector: 'test-list',
  templateUrl: './test-list.component.html'
})
export class TestListComponent {

  @Input()
  testList!: Test[]|undefined;
  @Input()
  idS!:string

  constructor(private _markavg: StudentService){
    getFromServerByActive(active:boolean){
      this._studentService.getFromServerByActive(active).subscribe(data=>{
        this.students=data;
      });
    }

     getFromServerMarksById(this.idS).subscribe(data);

  }

  markavg!: number;

}
