  selector: '[ltr]'
})
export class LtrDirective {


  @Input()
  language!:string;


  constructor(private _el:ElementRef) { 

  }

  ngOnInit(){
    if(this.language==="Hebbrew"){
      this._el.nativeElement.class="Hebbrew"

    }
  }
}
