import { Component } from '@angular/core';
import { Student } from '../modules/student/models/student.model';
import { Test } from '../modules/student/models/test.model';

@Component({
  selector: 'student-list',
  templateUrl: './student-list.component.html'

})
export class StudentListComponent {

  selectedStudent?: Student;
  selectedTest!: Test[];
  newStudent!: boolean;
  deleatStudent(student: string) {
    for (let s of this.students) {
      if (s.id == student) {
        let studentToDeleate = s;
        let studentToDeleate1 = this.students.indexOf(studentToDeleate);
        this.students.splice(studentToDeleate1, 1);
      }
    }
  }

  showDetails(student: Student) {

    this.selectedStudent = student;
    for (let s of this.students) {
      s.absentDays[0].sumdays = this._studentService.sumAbsantDays(s.idS);

    }


  }
  addStudent() {
    this.selectedStudent = new Student("", "", "", "", "", true, 0, [new Test("1234", new Date(2023, 12, 23), "English", 80)], [], Years.first, [{ idN: 0, numbersOfDays: 0, begginingDate: "", sumdays: 0 }]

    );

  }

  addNewStudentToList(studentToAdd: Student) {

    if (studentToAdd.idS == 0) {
      studentToAdd.idS = this.students.length + 1;
      studentToAdd.absentDays[0].idN = this.students.length + 1;
      //this.students.push(studentToAdd);
      // הוספה בסרוור שמחזירה מערך מעודכן
      this.postInServer(studentToAdd);


    }
    else {
      this.putInServer(studentToAdd.idS,studentToAdd);
      // let studentToUpdate = this.students.filter(x => x.idS == studentToAdd.idS)[0];
      // let index = this.students.indexOf(studentToUpdate);
      // this.students[index] = studentToAdd;

    }
    for (let s of this.students) {
      if (s.idS == studentToAdd.idS) {
        s.absentDays[0].sumdays = this._studentService.sumAbsantDays(s.absentDays[0].idN)


      }

    }
    this.selectedStudent = undefined;


  }


  getFromServer(){
    this._studentService.getFromServer().subscribe(data=>{
      this.students=data;
     });
  }
  getFromServerByActive(active:boolean){
    this._studentService.getFromServerByActive(active).subscribe(data=>{
      this.students=data;
    });
  }
  postInServer(student:Student){
    this._studentService.postInServer(student).subscribe(data=>{
     if(data){
      this.getFromServer();
     }
     else{
      alert("the adding didn't succeed")
     }
    })
  }
  putInServer(id:number, student:Student){
    this._studentService.putInServer(id,student).subscribe(data=>{
      if(data){
        this.getFromServer();
       }
       else{
        alert("the update didn't succeed")
       }
      })
  }
  deleatFromServer(){

  }
  showTest(tests: Test[], id: string) {
    this.selectedTest = tests;
    this.idS = id;

  }



  constructor(private _studentService: StudentService) {
    // _studentService.getPromiseStudent().then(studentList => {
    //   this.students = studentList;


    // })

   _studentService.getFromServer().subscribe(data=>{
    this.students=data;
   });
  }
  students!: Student[];
  idS!: string;
  // students: Student[] =this._studentService.getStudent();



}
